export interface IReader {
  read(contextDocs: Array<{ doc: string; score: number }>, question: string): Promise<string>
}

// Very small local reader to provide deterministic output for prototype
export class SimpleReader implements IReader {
  async read(contextDocs: Array<{ doc: string; score: number }>, question: string): Promise<string> {
    if (!contextDocs || contextDocs.length === 0) {
      return `I couldn't find relevant information to answer your question: ${question}`
    }
    // Return a concise synthesized answer from the top document as a stub for prototype
    const top = contextDocs[0]
    const snippet = top.doc
    return `Answer (based on retrieved docs): ${snippet.substring(0, Math.min(200, snippet.length))}${snippet.length > 200 ? '…' : ''}`
  }
}

