      darkItemSelectedColor: '#ffffff',
      itemHeight: 40,
      itemMarginInline: 0,
      itemBorderRadius: 0,
      iconSize: 16,
      fontSize: 14,
    },
    Layout: {
      siderBg: '#001529',
      headerBg: '#ffffff',
      headerHeight: 48,
    },
