export default function MainLayout() {
  const [collapsed, setCollapsed] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuthStore();

  // Tabs State
  const [tabs, setTabs] = useState<{ key: string, title: string, closable: boolean }[]>([
    { key: '/dashboard', title: '仪表板', closable: false }
  ]);

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
      setIsFullscreen(true);
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
        setIsFullscreen(false);
      }
    }
  };

  const menuItems: MenuProps['items'] = [
    {
      key: '/dashboard',
      icon: <LayoutDashboard size={18} />,
      label: '仪表板',
    },
    {
      key: '/rag',
      icon: <Database size={18} />,
      label: 'RAG 知识库',
    },
    {
      key: '/user-center',
      icon: <Users size={18} />,
      label: '用户中心',
    },
    {
      key: '/scheduler',
      icon: <Calendar size={18} />,
      label: '任务调度',
    },
    {
      key: '/mui-demo',
      icon: <Database size={18} />,
      label: '组件演示',
    },
    {
      key: '/settings',
      icon: <Settings size={18} />,
      label: '系统设置',
    },
  ];

  const userMenuItems: MenuProps['items'] = [
    {
      key: 'header',
      label: (
        <div className="px-1 py-1 cursor-default">
          <p className="font-semibold text-gray-800 m-0">{user?.username || 'Admin'}</p>
        </div>
      ),
      disabled: true,
      className: "hover:bg-transparent cursor-default",
    },
    { type: 'divider' },
    { key: 'profile', icon: <User size={14} />, label: '个人中心' },
    { key: 'settings', icon: <Settings size={14} />, label: '个人设置' },
    { type: 'divider' },
    { key: 'logout', icon: <LogOut size={14} />, label: '退出登录', danger: true },
  ];

  const handleMenuClick: MenuProps['onClick'] = (e) => {
    navigate(e.key);
  };

  const handleUserMenuClick: MenuProps['onClick'] = (e) => {
    if (e.key === 'logout') {
      logout();
      navigate('/login', { replace: true });
    }
  };

  const getBreadcrumbs = () => {
    const pathSnippets = location.pathname.split('/').filter(i => i);
    const extraBreadcrumbItems = pathSnippets.map((_, index) => {
      const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
      const foundItem = menuItems?.find((item: any) => item && item.key === url) as any;
      let title = pathSnippets[index].charAt(0).toUpperCase() + pathSnippets[index].slice(1);
      if (foundItem?.label) title = foundItem.label;

      const isLast = index === pathSnippets.length - 1;

      return { 
        key: url, 
        title: (
          <div className="flex items-center gap-1.5">
            {foundItem?.icon && (
              <span className="flex items-center text-gray-400 [&>svg]:w-4 [&>svg]:h-4">
                {foundItem.icon}
              </span>
            )}
            <span className={`transition-colors duration-200 ${isLast ? 'text-gray-900 font-medium' : 'text-gray-500 hover:text-[#0960bd]'}`}>
              {title}
            </span>
          </div>
        )
      };
    });

    return [
      { 
        key: '/dashboard', 
        title: (
          <div className="flex items-center gap-1.5">
            <LayoutDashboard size={16} className="text-gray-400" />
            <span className={`transition-colors duration-200 ${location.pathname === '/dashboard' ? 'text-gray-900 font-medium' : 'text-gray-500 hover:text-[#0960bd]'}`}>
              首页
            </span>
          </div>
        ) 
      }, 
      ...extraBreadcrumbItems
    ];
  };

  const updateTabs = () => {
    const path = location.pathname;
    if (path === '/' || path === '/login') return;

    setTabs(prev => {
      if (!Array.isArray(prev)) return [{ key: '/dashboard', title: '仪表板', closable: false }];
      if (prev.find(t => t.key === path)) return prev;

      const menuItem = menuItems?.find((item: any) => item.key === path) as any;
      const title = menuItem?.label || '未命名';
      return [...prev, { key: path, title, closable: true }];
    });
  };

