  LayoutDashboard,
  Database,
  Users,
  Calendar,
  Settings,
  Bell,
  LogOut,
  User,
  ChevronRight,
  Search,
  ChevronsLeft,
  ChevronsRight,
  Home,
  RotateCw,
  X,
  Maximize,
  Minimize,
  Github
} from 'lucide-react';
import { Outlet, useNavigate, useLocation } from 'react-router-dom';
import { useAuthStore } from '@/store/authStore';

const { Header, Sider, Content } = Layout;

const vbenThemeConfig = {
  token: {
    colorPrimary: '#0960bd',
    borderRadius: 2,
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    colorBgLayout: '#f0f2f5',
  },
  components: {
    Menu: {
      darkItemBg: '#001529',
      darkSubMenuItemBg: '#0c2135',
      darkItemSelectedBg: '#0960bd',
      darkItemColor: 'rgba(255, 255, 255, 0.7)',
      darkItemSelectedColor: '#ffffff',
      itemHeight: 40,
      itemMarginInline: 0,
      itemBorderRadius: 0,
      iconSize: 16,
      fontSize: 14,
    },
    Layout: {
      siderBg: '#001529',
      headerBg: '#ffffff',
      headerHeight: 48,
    },
    Breadcrumb: {
      itemColor: '#8b949e',
      lastItemColor: '#303133',
      separatorColor: '#d0d7de',
    }
  },
};

const TabsView = ({ 
  activeKey, 
  navigate,
  tabs = [], 
  onClose,
  onCloseOthers,
  onCloseAll
}: { 
  activeKey: string, 
  navigate: (path: string) => void,
  tabs?: Array<{ key: string, title: string, closable: boolean }>,
  onClose: (key: string) => void,
  onCloseOthers: () => void,
  onCloseAll: () => void
}) => {
  return (
    <div className="bg-[#f0f2f5] flex items-end pt-1 px-2 border-b border-gray-200 shadow-sm sticky top-[48px] z-10 select-none overflow-hidden" style={{ height: '32px' }}>
      <div className="flex-1 flex items-end overflow-x-auto no-scrollbar h-full gap-1">
        {(tabs || []).map((tab) => {
           const isActive = activeKey === tab.key || (tab.key === '/dashboard' && activeKey === '/');
           return (
             <div 
               key={tab.key}
               className={`
                  group relative flex items-center gap-2 px-3 py-1 text-xs cursor-pointer rounded-t-sm transition-all whitespace-nowrap border-t border-l border-r
                  ${isActive 
                    ? 'bg-white border-gray-200 text-[#0960bd] font-medium z-10 h-[calc(100%-1px)] translate-y-[1px]' 
                    : 'bg-transparent border-transparent text-gray-500 hover:bg-white hover:text-gray-700 h-[calc(100%-4px)] hover:h-[calc(100%-1px)]'
                  }
               `}
               onClick={() => navigate(tab.key)}
             >
                {tab.key === '/dashboard' && <Home size={12} className={isActive ? "text-[#0960bd]" : "text-gray-500"} />}
                <span>{tab.title}</span>
                {tab.closable && (
                  <X 
                    size={12} 
                    className={`ml-1 -mr-1 rounded-full p-0.5 box-content hover:bg-gray-200 hover:text-gray-700 transition-colors
                      ${isActive ? 'text-gray-400' : 'text-gray-400 opacity-0 group-hover:opacity-100'}
                    `}
                    onClick={(e) => {
                      e.stopPropagation();
                      onClose(tab.key);
                    }}
                  />
                )}
             </div>
           );
        })}
      </div>
      <div className="border-l border-gray-200 pl-2 ml-2 flex items-center gap-1 mb-1">
          <div 
            className="p-1 hover:bg-gray-200 rounded cursor-pointer text-gray-500 transition-colors"
            onClick={() => window.location.reload()}
          >
            <RotateCw size={14} />
          </div>
          <Dropdown menu={{ items: [
            { key: '1', label: '关闭其他', onClick: onCloseOthers }, 
            { key: '2', label: '关闭全部', onClick: onCloseAll }
          ] }}>
             <div className="p-1 hover:bg-gray-200 rounded cursor-pointer text-gray-500 transition-colors">
                <ChevronRight size={14} className="rotate-90" />
             </div>
          </Dropdown>
      </div>
    </div>
  );
};

