    colorPrimary: '#0960bd',
    borderRadius: 2,
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    colorBgLayout: '#f0f2f5',
  },
  components: {
    Menu: {
      darkItemBg: '#001529',
      darkSubMenuItemBg: '#0c2135',
      darkItemSelectedBg: '#0960bd',
      darkItemColor: 'rgba(255, 255, 255, 0.7)',
      darkItemSelectedColor: '#ffffff',
      itemHeight: 40,
      itemMarginInline: 0,
      itemBorderRadius: 0,
      iconSize: 16,
      fontSize: 14,
    },
    Layout: {
      siderBg: '#001529',
      headerBg: '#ffffff',
      headerHeight: 48,
    },
    Breadcrumb: {
      itemColor: '#8b949e',
      lastItemColor: '#303133',
      separatorColor: '#d0d7de',
    }
  },
};

const TabsView = ({ 
  activeKey, 
  navigate,
  tabs = [], 
  onClose,
  onCloseOthers,
  onCloseAll
}: { 
  activeKey: string, 
  navigate: (path: string) => void,
  tabs?: Array<{ key: string, title: string, closable: boolean }>,
  onClose: (key: string) => void,
  onCloseOthers: () => void,
  onCloseAll: () => void
}) => {
  return (
    <div className="bg-[#f0f2f5] flex items-end pt-1 px-2 border-b border-gray-200 shadow-sm sticky top-[48px] z-10 select-none overflow-hidden" style={{ height: '32px' }}>
      <div className="flex-1 flex items-end overflow-x-auto no-scrollbar h-full gap-1">
        {(tabs || []).map((tab) => {
           const isActive = activeKey === tab.key || (tab.key === '/dashboard' && activeKey === '/');
           return (
             <div 
               key={tab.key}
               className={`
                  group relative flex items-center gap-2 px-3 py-1 text-xs cursor-pointer rounded-t-sm transition-all whitespace-nowrap border-t border-l border-r
                  ${isActive 
                    ? 'bg-white border-gray-200 text-[#0960bd] font-medium z-10 h-[calc(100%-1px)] translate-y-[1px]' 
                    : 'bg-transparent border-transparent text-gray-500 hover:bg-white hover:text-gray-700 h-[calc(100%-4px)] hover:h-[calc(100%-1px)]'
                  }
               `}
               onClick={() => navigate(tab.key)}
             >
                {tab.key === '/dashboard' && <Home size={12} className={isActive ? "text-[#0960bd]" : "text-gray-500"} />}
                <span>{tab.title}</span>
                {tab.closable && (
                  <X 
                    size={12} 
                    className={`ml-1 -mr-1 rounded-full p-0.5 box-content hover:bg-gray-200 hover:text-gray-700 transition-colors
                      ${isActive ? 'text-gray-400' : 'text-gray-400 opacity-0 group-hover:opacity-100'}
                    `}
                    onClick={(e) => {
                      e.stopPropagation();
                      onClose(tab.key);
                    }}
                  />
                )}
             </div>
           );
        })}
      </div>
      <div className="border-l border-gray-200 pl-2 ml-2 flex items-center gap-1 mb-1">
          <div 
            className="p-1 hover:bg-gray-200 rounded cursor-pointer text-gray-500 transition-colors"
            onClick={() => window.location.reload()}
          >
            <RotateCw size={14} />
          </div>
          <Dropdown menu={{ items: [
            { key: '1', label: '关闭其他', onClick: onCloseOthers }, 
            { key: '2', label: '关闭全部', onClick: onCloseAll }
          ] }}>
             <div className="p-1 hover:bg-gray-200 rounded cursor-pointer text-gray-500 transition-colors">
                <ChevronRight size={14} className="rotate-90" />
             </div>
          </Dropdown>
      </div>
    </div>
  );
};

export default function MainLayout() {
  const [collapsed, setCollapsed] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuthStore();

  // Tabs State
  const [tabs, setTabs] = useState<{ key: string, title: string, closable: boolean }[]>([
    { key: '/dashboard', title: '仪表板', closable: false }
  ]);

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
      setIsFullscreen(true);
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
        setIsFullscreen(false);
      }
    }
  };

  const menuItems: MenuProps['items'] = [
    {
      key: '/dashboard',
      icon: <LayoutDashboard size={18} />,
      label: '仪表板',
    },
    {
      key: '/rag',
      icon: <Database size={18} />,
      label: 'RAG 知识库',
    },
    {
      key: '/user-center',
      icon: <Users size={18} />,
      label: '用户中心',
    },
    {
      key: '/scheduler',
      icon: <Calendar size={18} />,
      label: '任务调度',
    },
    {
      key: '/mui-demo',
      icon: <Database size={18} />,
      label: '组件演示',
    },
    {
      key: '/settings',
      icon: <Settings size={18} />,
      label: '系统设置',
    },
  ];

  const userMenuItems: MenuProps['items'] = [
    {
      key: 'header',
      label: (
        <div className="px-1 py-1 cursor-default">
          <p className="font-semibold text-gray-800 m-0">{user?.username || 'Admin'}</p>
        </div>
      ),
      disabled: true,
      className: "hover:bg-transparent cursor-default",
    },
    { type: 'divider' },
    { key: 'profile', icon: <User size={14} />, label: '个人中心' },
    { key: 'settings', icon: <Settings size={14} />, label: '个人设置' },
    { type: 'divider' },
    { key: 'logout', icon: <LogOut size={14} />, label: '退出登录', danger: true },
  ];

  const handleMenuClick: MenuProps['onClick'] = (e) => {
    navigate(e.key);
  };

  const handleUserMenuClick: MenuProps['onClick'] = (e) => {
    if (e.key === 'logout') {
      logout();
      navigate('/login', { replace: true });
    }
  };

  const getBreadcrumbs = () => {
    const pathSnippets = location.pathname.split('/').filter(i => i);
    const extraBreadcrumbItems = pathSnippets.map((_, index) => {
      const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
      const foundItem = menuItems?.find((item: any) => item && item.key === url) as any;
      let title = pathSnippets[index].charAt(0).toUpperCase() + pathSnippets[index].slice(1);
      if (foundItem?.label) title = foundItem.label;

      const isLast = index === pathSnippets.length - 1;

      return { 
        key: url, 
        title: (
          <div className="flex items-center gap-1.5 leading-none">
            {foundItem?.icon && (
              <span className="flex items-center text-gray-400 [&>svg]:w-4 [&>svg]:h-4">
                {foundItem.icon}
              </span>
            )}
            <span className={`transition-colors duration-200 flex items-center ${isLast ? 'text-gray-900 font-medium' : 'text-gray-500 hover:text-[#0960bd]'}`}>
              {title}
            </span>
          </div>
        )
      };
    });

    return [
      { 
        key: '/dashboard', 
        title: (
          <div className="flex items-center gap-1.5 leading-none">
            <LayoutDashboard size={16} className="text-gray-400" />
            <span className={`transition-colors duration-200 flex items-center ${location.pathname === '/dashboard' ? 'text-gray-900 font-medium' : 'text-gray-500 hover:text-[#0960bd]'}`}>
              首页
            </span>
          </div>
        ) 
      }, 
      ...extraBreadcrumbItems
    ];
  };

  const updateTabs = () => {
    const path = location.pathname;
    if (path === '/' || path === '/login') return;

    setTabs(prev => {
      if (!Array.isArray(prev)) return [{ key: '/dashboard', title: '仪表板', closable: false }];
      if (prev.find(t => t.key === path)) return prev;

      const menuItem = menuItems?.find((item: any) => item.key === path) as any;
      const title = menuItem?.label || '未命名';
      return [...prev, { key: path, title, closable: true }];
    });
  };

  useEffect(() => {
    updateTabs();
  }, [location.pathname]);

  const handleCloseTab = (key: string) => {
    const newTabs = tabs.filter(t => t.key !== key);
    setTabs(newTabs);

    // If closing active tab, navigate to the last one
    if (key === location.pathname) {
        const lastTab = newTabs[newTabs.length - 1];
        if (lastTab) navigate(lastTab.key);
    }
  };

  const handleCloseOthers = () => {
    const current = tabs.find(t => t.key === location.pathname) || tabs[0];
    if (current.key === '/dashboard') {
        setTabs([{ key: '/dashboard', title: '仪表板', closable: false }]);
    } else {
        setTabs([
            { key: '/dashboard', title: '仪表板', closable: false },
            { ...current, closable: true }
        ]);
    }
  };

  const handleCloseAll = () => {
    setTabs([{ key: '/dashboard', title: '仪表板', closable: false }]);
    navigate('/dashboard');
  };

  return (
    <ConfigProvider theme={vbenThemeConfig}>
      <Layout className="min-h-screen flex flex-row">
        <Sider
          trigger={null}
          collapsible
          collapsed={collapsed}
          width={210}
          className="shadow-xl z-30 !fixed left-0 top-0 bottom-0 h-full overflow-hidden"
          style={{ backgroundColor: '#001529' }}
        >
          <div className="h-[48px] flex items-center justify-center relative overflow-hidden transition-all duration-300 bg-[#001529] shadow-sm">
            <div className={`flex items-center gap-3 transition-all duration-300 ${collapsed ? 'justify-center w-full' : 'w-full px-4'}`}>
              <div className="w-8 h-8 flex items-center justify-center shrink-0">
                 <div className="w-7 h-7 bg-[#0960bd] rounded-sm flex items-center justify-center text-white font-bold text-lg">S</div>
              </div>
              {!collapsed && (
                <span className="font-bold text-lg text-white tracking-wide whitespace-nowrap opacity-100 transition-opacity duration-300">
                  Sentinel<span className="text-[#0960bd]">Admin</span>
                </span>
              )}
            </div>
          </div>

          <Menu
            theme="dark"
            mode="inline"
            selectedKeys={[location.pathname]}
            items={menuItems}
            onClick={handleMenuClick}
            className="border-r-0 font-medium mt-2 select-none"
          />
        </Sider>

        <Layout 
          className="bg-[#f0f2f5] transition-all duration-300 flex flex-col min-h-screen"
          style={{ marginLeft: collapsed ? 80 : 210 }}
        >
          <Header 
            className="bg-white px-0 flex items-center justify-between sticky top-0 z-20 shadow-sm border-b border-gray-100"
            style={{ height: '48px', lineHeight: '48px' }}
          >
            <div className="flex items-center h-full">
              <div 
                onClick={() => setCollapsed(!collapsed)}
                className="h-full flex items-center justify-center px-3 hover:bg-gray-50 cursor-pointer text-gray-600 transition-colors"
              >
                {collapsed ? <ChevronsRight size={18} /> : <ChevronsLeft size={18} />}
              </div>

              <Breadcrumb 
                items={getBreadcrumbs()} 
                className="flex text-sm text-gray-600 ml-2 items-center !mb-0"
                separator={<ChevronRight size={14} className="text-gray-400" />}
              />
            </div>

            <div className="flex items-center h-full pr-4 gap-1">
              <Tooltip title="搜索">
                <div className="h-full px-2.5 flex items-center hover:bg-gray-50 cursor-pointer text-gray-600 transition-colors">
                  <Search size={16} />
                </div>
              </Tooltip>

              <Tooltip title="源码">
                 <div className="h-full px-2.5 flex items-center hover:bg-gray-50 cursor-pointer text-gray-600 transition-colors">
                   <Github size={16} />
                 </div>
              </Tooltip>

              <Tooltip title={isFullscreen ? "退出全屏" : "全屏"}>
                 <div onClick={toggleFullscreen} className="h-full px-2.5 flex items-center hover:bg-gray-50 cursor-pointer text-gray-600 transition-colors">
                   {isFullscreen ? <Minimize size={16} /> : <Maximize size={16} />}
                 </div>
              </Tooltip>

              <Tooltip title="通知">
                <div className="h-full px-2.5 flex items-center hover:bg-gray-50 cursor-pointer relative text-gray-600">
                    <Bell size={16} />
                    <span className="absolute top-3 right-2 w-1.5 h-1.5 bg-red-500 rounded-full border border-white"></span>
                </div>
              </Tooltip>

              <Dropdown
                menu={{ items: userMenuItems, onClick: handleUserMenuClick }}
                placement="bottomRight"
                trigger={['hover']}
              >
                <div className="h-full pl-2 pr-3 flex items-center gap-2 hover:bg-gray-50 cursor-pointer transition-colors">
                  <Avatar size={24} src={user?.avatar} className="bg-[#0960bd]">
                    {user?.username?.charAt(0).toUpperCase() || 'A'}
                  </Avatar>
                  <span className="hidden sm:block text-sm font-medium text-gray-700">{user?.username || 'Admin'}</span>
                </div>
              </Dropdown>

              <Tooltip title="系统设置">
                 <div className="h-full px-2.5 flex items-center hover:bg-gray-50 cursor-pointer text-gray-600 transition-colors">
                   <Settings size={16} />
                 </div>
              </Tooltip>
            </div>
          </Header>

          <TabsView 
            activeKey={location.pathname} 
            navigate={navigate}
            tabs={tabs}
            onClose={handleCloseTab}
            onCloseOthers={handleCloseOthers}
            onCloseAll={handleCloseAll}
          />

          <Content className="p-4 overflow-auto min-h-0 bg-[#f0f2f5] flex-1">
             <div className="w-full h-full animate-fade-in">
                <Outlet />
             </div>
          </Content>

          <div className="text-center py-4 text-gray-400 text-xs">
            Copyright ©{new Date().getFullYear()} Vben Admin Style
          </div>
        </Layout>
      </Layout>
    </ConfigProvider>
  );
}

