@import 'tailwindcss';

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  background-color: #f0f2f5;
  color: #333;
}

#root {
  min-height: 100vh;
}

/* Ant Design Overrides to match Vben */
.ant-card {
  border-radius: 2px !important;
}

.ant-btn {
  border-radius: 2px !important;
  box-shadow: none !important;
}

.ant-btn-primary {
  background-color: #0960bd !important;
  border-color: #0960bd !important;
}

.ant-btn-primary:hover {
  background-color: #0c74e0 !important;
  border-color: #0c74e0 !important;
  opacity: 0.9;
}

.ant-menu-dark .ant-menu-item-selected {
  background-color: #0960bd !important;
}

/* Custom Scrollbar */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: rgba(144, 147, 153, 0.3);
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: rgba(144, 147, 153, 0.5);
}

/* Fade In Animation */
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.animate-fade-in {
  animation: fadeIn 0.3s ease-out forwards;
}

/* Hide scrollbar for TabsView */
.no-scrollbar::-webkit-scrollbar {
  display: none;
}
.no-scrollbar {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

/* Breadcrumb alignment fixes */
.ant-breadcrumb {
  display: flex !important;
  align-items: center !important;
  line-height: 1 !important;
}

.ant-breadcrumb ol {
  display: flex !important;
  align-items: center !important;
  width: 100%;
}

.ant-breadcrumb li {
  display: flex !important;
  align-items: center !important;
}

.ant-breadcrumb-separator {
  display: flex !important;
  align-items: center !important;
  justify-content: center !important;
  margin-inline: 4px !important; 
}

.ant-breadcrumb-link {
  display: flex !important;
  align-items: center !important;
}

.ant-breadcrumb-link > div {
  display: flex !important;
  align-items: center !important;
}

