import { Card, Row, Col, Table, Progress, Button, Tag, Space, Breadcrumb } from 'antd';
import type { ColumnsType } from 'antd/es/table';
import {
  Users,
  Database,
  Activity,
  CheckCircle,
  MoreHorizontal,
  FileText,
  Clock,
  Shield,
  Download,
  RotateCw
} from 'lucide-react';
import { format } from 'date-fns';
import { zhCN } from 'date-fns/locale';

interface TaskData {
  key: string;
  name: string;
  status: string;
  progress: number;
  time: string;
  id: string;
}

const taskColumns: ColumnsType<TaskData> = [
  {
    title: '任务名称',
    dataIndex: 'name',
    key: 'name',
    render: (text) => (
      <div className="flex items-center gap-2">
        <span className="font-medium text-gray-700">{text}</span>
      </div>
    ),
  },
  {
    title: '任务ID',
    dataIndex: 'id',
    key: 'id',
    render: (text) => <span className="text-gray-400 text-xs font-mono">{text}</span>,
  },
  {
    title: '状态',
    dataIndex: 'status',
    key: 'status',
    render: (status: string) => {
      let color = 'default';
      if (status === '运行中') color = 'processing';
      if (status === '已完成') color = 'success';
      if (status === '等待中') color = 'warning';

      return <Tag color={color} bordered={false}>{status}</Tag>;
    },
  },
  {
    title: '进度',
    dataIndex: 'progress',
    key: 'progress',
    width: 200,
    render: (progress: number) => (
      <Progress 
        percent={progress} 
        size="small" 
        strokeColor="#0960bd"
        trailColor="#f5f5f5"
      />
    ),
  },
  {
    title: '时间',
    dataIndex: 'time',
    key: 'time',
    render: (text) => (
        <div className="flex items-center text-gray-400 text-xs gap-1">
            <Clock size={12} />
            <span>{text}</span>
        </div>
    ),
  },
];

const taskData: TaskData[] = [
  {
    key: '1',
    id: 'TSK-2023-001',
    name: '数据同步任务',
    status: '运行中',
    progress: 65,
    time: '2分钟前',
  },
  {
    key: '2',
    id: 'TSK-2023-002',
    name: 'RAG 索引构建',
    status: '已完成',
    progress: 100,
    time: '10分钟前',
  },
  {
    key: '3',
    id: 'TSK-2023-003',
    name: '报表生成',
    status: '等待中',
    progress: 0,
