  {
    key: '4',
    id: 'TSK-2023-004',
    name: '数据备份',
    status: '已完成',
    progress: 100,
    time: '1小时前',
  },
];

// Vben Analysis Card Style
const StatCard = ({ 
  title, 
  value, 
  icon: Icon, 
  colorClass, 
  bgClass,
  actionText
}: { 
  title: string; 
  value: string | number; 
  icon: any; 
  colorClass: string; 
  bgClass: string;
