interface TaskData {
  key: string;
  name: string;
  status: string;
  progress: number;
  time: string;
  id: string;
}

const taskColumns: ColumnsType<TaskData> = [
  {
    title: '任务名称',
    dataIndex: 'name',
    key: 'name',
    render: (text) => (
      <div className="flex items-center gap-2">
        <span className="font-medium text-gray-700">{text}</span>
      </div>
    ),
  },
  {
    title: '任务ID',
    dataIndex: 'id',
    key: 'id',
    render: (text) => <span className="text-gray-400 text-xs font-mono">{text}</span>,
  },
  {
    title: '状态',
    dataIndex: 'status',
    key: 'status',
    render: (status: string) => {
      let color = 'default';
      if (status === '运行中') color = 'processing';
      if (status === '已完成') color = 'success';
      if (status === '等待中') color = 'warning';

      return <Tag color={color} bordered={false}>{status}</Tag>;
    },
  },
  {
    title: '进度',
    dataIndex: 'progress',
    key: 'progress',
    width: 200,
    render: (progress: number) => (
      <Progress 
        percent={progress} 
        size="small" 
        strokeColor="#0960bd"
        trailColor="#f5f5f5"
      />
    ),
  },
  {
    title: '时间',
    dataIndex: 'time',
    key: 'time',
    render: (text) => (
        <div className="flex items-center text-gray-400 text-xs gap-1">
            <Clock size={12} />
