  RotateCw,
  X
} from 'lucide-react';
import { Outlet, useNavigate, useLocation } from 'react-router-dom';
import { useAuthStore } from '@/store/authStore';

const { Header, Sider, Content } = Layout;

const themeConfig = {
  token: {
    colorPrimary: '#0960bd',
    borderRadius: 2,
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
  },
  components: {
    Menu: {
      darkItemBg: '#001529',
      darkSubMenuItemBg: '#0c2135',
      darkItemSelectedBg: '#0960bd',
      darkItemColor: 'rgba(255, 255, 255, 0.7)',
      darkItemSelectedColor: '#ffffff',
      itemHeight: 40,
      itemMarginInline: 0,
      itemBorderRadius: 0,
    },
  },
};

export default function MainLayout() {
  const [collapsed, setCollapsed] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuthStore();

  const menuItems: MenuProps['items'] = [
    {
      key: '/dashboard',
      icon: <LayoutDashboard size={18} />,
      label: '仪表板',
    },
    {
      key: '/mui-demo',
      icon: <Database size={18} />,
      label: 'MUI 组件',
    },
    {
      key: '/env-demo',
      icon: <Settings size={18} />,
      label: '环境配置',
    },
    {
      key: '/rag',
      icon: <Database size={18} />,
      label: 'RAG 知识库',
    },
    {
      key: '/user-center',
      icon: <Users size={18} />,
      label: '用户中心',
    },
    {
      key: '/scheduler',
      icon: <Calendar size={18} />,
      label: '任务调度',
    },
    {
      key: '/settings',
      icon: <Settings size={18} />,
      label: '系统设置',
    },
  ];

  const userMenuItems: MenuProps['items'] = [
    {
      key: 'header',
      label: (
        <div className="px-1 py-2 cursor-default">
          <p className="font-semibold text-gray-800 m-0">{user?.username || '用户'}</p>
          <p className="text-xs text-gray-500 m-0 mt-0.5">{user?.email || 'admin@sentinel.com'}</p>
        </div>
      ),
      disabled: true,
      className: "hover:bg-transparent cursor-default",
    },
    { type: 'divider' },
    {
      key: 'profile',
      icon: <User size={16} />,
      label: '个人资料',
    },
    {
      key: 'settings',
      icon: <Settings size={16} />,
      label: '账户设置',
    },
    { type: 'divider' },
    {
      key: 'logout',
      icon: <LogOut size={16} />,
      label: '退出登录',
      danger: true,
    },
  ];

  const handleMenuClick: MenuProps['onClick'] = (e) => {
    navigate(e.key);
  };

  const handleUserMenuClick: MenuProps['onClick'] = (e) => {
    if (e.key === 'logout') {
      logout();
      navigate('/login', { replace: true });
    } else if (e.key === 'profile') {
      navigate('/profile');
    } else if (e.key === 'settings') {
      navigate('/settings');
    }
  };

  const getBreadcrumbs = () => {
    const pathSnippets = location.pathname.split('/').filter(i => i);
    const extraBreadcrumbItems = pathSnippets.map((_, index) => {
      const url = `/${pathSnippets.slice(0, index + 1).join('/')}`;
      const foundItem = menuItems?.find((item: any) => item && item.key === url) as any;
      let title = pathSnippets[index].charAt(0).toUpperCase() + pathSnippets[index].slice(1);
      if (foundItem?.label && typeof foundItem.label === 'string') {
        title = foundItem.label;
      }
      return { key: url, title: title };
    });
    return [{ key: '/dashboard', title: '首页' }, ...extraBreadcrumbItems];
  };

  const demoTabs = [
    { key: '/dashboard', title: '仪表板', active: location.pathname === '/dashboard' || location.pathname === '/' },
    { key: '/system', title: '系统管理', active: false },
    { key: '/user', title: '个人中心', active: false },
  ];

  return (
    <ConfigProvider theme={themeConfig}>
      <Layout className="min-h-screen">
        <Sider
          trigger={null}
          collapsible
          collapsed={collapsed}
          width={210}
          className="shadow-xl z-20"
          style={{ backgroundColor: '#001529' }}
        >
          <div className="h-12 flex items-center justify-center relative overflow-hidden transition-all duration-300 mx-0 my-0 bg-[#001529]">
            <div className={`flex items-center gap-2.5 transition-all duration-300 ${collapsed ? 'justify-center w-full' : 'w-full px-4'}`}>
              <div className="w-8 h-8 rounded-lg flex items-center justify-center shrink-0">
                 <div className="w-7 h-7 bg-blue-600 rounded flex items-center justify-center text-white font-bold">S</div>
              </div>
              {!collapsed && (
                <span className="font-bold text-lg text-white tracking-wide whitespace-nowrap opacity-100 transition-opacity duration-300">
                  Sentinel<span className="text-blue-500">X</span>
                </span>
              )}
            </div>
          </div>

          <Menu
            theme="dark"
            mode="inline"
            selectedKeys={[location.pathname]}
            items={menuItems}
            onClick={handleMenuClick}
            className="border-r-0 font-medium mt-2"
          />
        </Sider>

        <Layout className="bg-[#f0f2f5] transition-all duration-300">
          <Header 
            className="bg-white px-4 flex items-center justify-between sticky top-0 z-10 border-b border-gray-100/50"
            style={{ height: '48px', paddingInline: '16px', lineHeight: '48px' }}
          >
            <div className="flex items-center gap-4">
              <button
                onClick={() => setCollapsed(!collapsed)}
                className="flex items-center justify-center p-1.5 rounded hover:bg-gray-100 text-gray-500 transition-colors"
              >
                {collapsed ? <ChevronsRight size={18} /> : <ChevronsLeft size={18} />}
              </button>

              <Breadcrumb 
                items={getBreadcrumbs()} 
                className="hidden md:flex text-sm text-gray-500"
                separator={<ChevronRight size={14} className="text-gray-400" />}
              />
            </div>

            <div className="flex items-center gap-3">
              <div className="hidden md:flex items-center hover:bg-gray-100 rounded-full px-2 py-1 transition-colors cursor-pointer">
                <Search size={16} className="text-gray-500" />
              </div>

              <Tooltip title="通知">
                <div className="flex items-center justify-center w-8 h-8 rounded-full hover:bg-gray-100 cursor-pointer relative">
                    <Bell size={18} className="text-gray-600" />
                    <span className="absolute top-1.5 right-1.5 w-2 h-2 bg-red-500 rounded-full border-2 border-white"></span>
                </div>
              </Tooltip>

              <Dropdown
                menu={{ items: userMenuItems, onClick: handleUserMenuClick }}
                placement="bottomRight"
                trigger={['click']}
              >
                <div className="flex items-center gap-2 cursor-pointer pl-2 hover:bg-gray-50 py-1 px-2 rounded transition-colors">
                  <Avatar size={24} src={user?.avatar} className="bg-blue-600">
                    {user?.username?.charAt(0).toUpperCase() || 'U'}
                  </Avatar>
                  <span className="hidden sm:block text-sm font-medium text-gray-700">{user?.username || 'Admin'}</span>
                </div>
              </Dropdown>
            </div>
          </Header>

          <div className="bg-white border-b border-gray-200 px-3 flex items-center gap-2 shadow-sm sticky z-10" style={{ top: 48, height: '34px' }}>
            {demoTabs.map((tab) => (
               <div 
                 key={tab.key}
                 className={`
                    group flex items-center gap-2 px-3 py-1 text-xs cursor-pointer border-r border-gray-100 h-full transition-all
                    ${tab.active ? 'text-blue-600 font-medium bg-blue-50/50' : 'text-gray-600 hover:text-gray-800 hover:bg-gray-50'}
                 `}
                 onClick={() => {
                    if (tab.key === '/dashboard') navigate('/dashboard');
                 }}
               >
                  {tab.key === '/dashboard' && <Home size={12} className="mb-0.5" />}
                  <span>{tab.title}</span>
                  {tab.active && <span className="absolute bottom-0 left-0 w-full h-[2px] bg-blue-600"></span>}
                  {!tab.active && (
                    <X size={12} className="text-gray-400 opacity-0 group-hover:opacity-100 hover:text-red-500 transition-opacity" />
                  )}
               </div>
            ))}
            <div className="ml-auto flex items-center px-2 cursor-pointer text-gray-400 hover:text-blue-600">
                <RotateCw size={14} />
            </div>
          </div>

          <Content className="p-4 overflow-auto min-h-0 bg-[#f0f2f5]">
             <div className="w-full h-full">
                <Outlet />
             </div>
          </Content>
        </Layout>
      </Layout>
    </ConfigProvider>
  );
}

