                    { title: '工作台' },
                ]}
                className="text-xs text-gray-500"
            />
         </div>
         <div className="flex items-center gap-4 text-right">
             <div className="hidden md:block">
                 <p className="text-gray-400 text-xs mb-0.5">当前时间</p>
                 <p className="text-gray-700 font-medium text-sm">{currentDate}</p>
             </div>
             <div className="flex gap-2">
                 <Button icon={<RotateCw size={14} />} size="small">刷新</Button>
                 <Button type="primary" size="small" className="bg-[#0960bd]">配置</Button>
             </div>
         </div>
      </div>

      <Row gutter={[16, 16]}>
        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="总用户数" 
            value="1,243" 
            icon={Users} 
            colorClass="text-blue-600" 
            bgClass="bg-blue-50"
            actionText="较昨日 +12"
          />
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="知识库文档" 
            value="8,456" 
            icon={Database} 
            colorClass="text-emerald-600" 
            bgClass="bg-emerald-50"
            actionText="每日更新" 
          />
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="系统负载" 
            value="42%" 
            icon={Activity} 
            colorClass="text-amber-600" 
            bgClass="bg-amber-50"
            actionText="运行正常"
          />
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="安全评分" 
            value="98" 
            icon={Shield} 
            colorClass="text-purple-600" 
            bgClass="bg-purple-50"
            actionText="上次扫描: 1小时前"
          />
        </Col>
      </Row>

      <Row gutter={[16, 16]}>
        <Col xs={24} lg={16}>
          <Card 
            title="最新任务" 
            bordered={false}
            className="shadow-sm rounded-sm header-solid h-full"
            headStyle={{ borderBottom: '1px solid #f0f0f0', minHeight: '48px' }}
            bodyStyle={{ padding: '0' }}
            extra={<Button type="link" size="small">查看全部</Button>}
          >
            <Table
              columns={taskColumns}
              dataSource={taskData}
              pagination={false}
              size="middle"
              className="vben-table"
              rowClassName="hover:bg-blue-50/30 transition-colors"
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card 
            title="快捷操作" 
            bordered={false}
            className="shadow-sm rounded-sm h-full"
            headStyle={{ borderBottom: '1px solid #f0f0f0', minHeight: '48px' }}
          >
            <div className="grid grid-cols-3 gap-4 py-4">
                {[
                    { label: '添加用户', icon: Users, color: '#0960bd' },
                    { label: '系统分析', icon: Activity, color: '#10b981' },
                    { label: '权限管理', icon: Shield, color: '#f59e0b' },
                    { label: '文档上传', icon: FileText, color: '#8b5cf6' },
                    { label: '报表导出', icon: Download, color: '#ef4444' },
                    { label: '更多', icon: MoreHorizontal, color: '#6b7280' },
                ].map((item, idx) => (
                    <div key={idx} className="flex flex-col items-center justify-center p-3 rounded hover:bg-gray-50 cursor-pointer transition-colors group">
                        <div className="w-10 h-10 rounded-lg flex items-center justify-center mb-2 transition-transform group-hover:scale-110" style={{ backgroundColor: `${item.color}15`, color: item.color }}>
                            <item.icon size={20} />
                        </div>
                        <span className="text-xs text-gray-600">{item.label}</span>
                    </div>
                ))}
            </div>

            <div className="mt-4 pt-4 border-t border-gray-100">
                <h4 className="text-xs font-bold text-gray-400 mb-3 uppercase tracking-wider">系统健康度</h4>
                <div className="space-y-4">
                    {[
                        { label: 'CPU', val: 45, color: '#10b981' },
                        { label: 'RAM', val: 68, color: '#3b82f6' },
                    ].map((item, i) => (
                        <div key={i} className="flex items-center gap-3">
                            <span className="text-xs text-gray-500 w-8">{item.label}</span>
                            <Progress percent={item.val} strokeColor={item.color} size="small" showInfo={false} className="mb-0 flex-1" />
                            <span className="text-xs text-gray-700 w-8 text-right">{item.val}%</span>
                        </div>
                    ))}
                </div>
            </div>
          </Card>
        </Col>
      </Row>
    </div>
  );
}
