import { Card, Row, Col, Statistic, Table, Progress, Button } from 'antd';
import type { ColumnsType } from 'antd/es/table';
import {
  ArrowUpRight,
  ArrowDownRight,
  Users,
  Database,
  Activity,
  CheckCircle,
  Plus,
  Upload,
  MoreHorizontal,
  FileText
} from 'lucide-react';

interface TaskData {
  key: string;
  name: string;
  status: string;
  progress: number;
  time: string;
}

const taskColumns: ColumnsType<TaskData> = [
  {
    title: '任务名称',
    dataIndex: 'name',
    key: 'name',
    render: (text) => (
      <div className="flex items-center gap-2">
        <div className="p-2 rounded-lg bg-gray-50 text-gray-500">
          <FileText size={16} />
        </div>
        <span className="font-medium text-gray-700">{text}</span>
      </div>
    ),
  },
  {
    title: '状态',
    dataIndex: 'status',
    key: 'status',
    render: (status: string) => (
      <span
        className={`px-3 py-1 rounded-full text-xs font-medium border ${
          status === '运行中'
            ? 'bg-blue-50 text-blue-600 border-blue-100'
            : status === '已完成'
              ? 'bg-emerald-50 text-emerald-600 border-emerald-100'
              : 'bg-amber-50 text-amber-600 border-amber-100'
        }`}
      >
        {status}
      </span>
    ),
  },
  {
    title: '进度',
    dataIndex: 'progress',
    key: 'progress',
    render: (progress: number) => (
      <Progress 
        percent={progress} 
        size="small" 
        strokeColor={{
          '0%': '#3b82f6',
          '100%': '#8b5cf6',
        }}
        trailColor="#f3f4f6"
      />
    ),
  },
  {
    title: '时间',
    dataIndex: 'time',
    key: 'time',
    render: (text) => <span className="text-gray-400 text-sm">{text}</span>,
  },
];

const taskData: TaskData[] = [
  {
    key: '1',
    name: '数据同步任务',
    status: '运行中',
    progress: 65,
    time: '2分钟前',
  },
  {
    key: '2',
    name: 'RAG 索引构建',
    status: '已完成',
    progress: 100,
    time: '10分钟前',
  },
  {
    key: '3',
    name: '报表生成',
    status: '等待中',
    progress: 0,
    time: '15分钟前',
  },
  {
    key: '4',
    name: '数据备份',
    status: '已完成',
    progress: 100,
    time: '1小时前',
  },
];

const StatCard = ({ 
  title, 
  value, 
  trend, 
  trendUp, 
  icon: Icon, 
  colorClass, 
  bgClass 
}: { 
  title: string; 
  value: string | number; 
  trend: string; 
  trendUp: boolean; 
  icon: any; 
  colorClass: string; 
  bgClass: string;
}) => (
  <Card className="border-0 shadow-sm hover:shadow-md transition-all duration-300 rounded-2xl overflow-hidden h-full group">
    <div className="flex items-center justify-between">
      <div className="flex flex-col gap-2">
        <p className="text-gray-500 text-sm font-medium">{title}</p>
        <div className="text-2xl font-bold text-gray-800 mt-1">{value}</div>
        <div className={`flex items-center text-sm font-medium ${trendUp ? 'text-emerald-600' : 'text-rose-600'}`}>
          {trendUp ? <ArrowUpRight size={16} /> : <ArrowDownRight size={16} />}
          <span className="ml-1">{trend}</span>
        </div>
      </div>
      <div className={`h-14 w-14 rounded-xl ${bgClass} flex items-center justify-center ${colorClass} group-hover:scale-110 transition-transform duration-300`}>
        <Icon size={28} />
      </div>
    </div>
  </Card>
);

export default function Dashboard() {
  return (
    <div className="space-y-6">
      {/* Welcome Header */}
      <div className="relative overflow-hidden rounded-2xl bg-gradient-to-r from-indigo-600 to-violet-600 p-8 text-white shadow-lg">
        <div className="relative z-10 flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold mb-2">仪表板</h1>
            <p className="text-indigo-100 text-lg opacity-90">欢迎回到 Sentinel-X 管理平台，祝您今天工作愉快！</p>
          </div>
          <div className="flex gap-3">
            <Button 
              type="primary" 
              icon={<Plus size={18} />} 
              className="bg-white/20 border-0 hover:bg-white/30 text-white h-10 px-6 rounded-xl backdrop-blur-sm"
            >
              新建任务
            </Button>
            <Button 
              icon={<Upload size={18} />} 
              className="bg-white text-indigo-600 border-0 hover:bg-indigo-50 h-10 px-6 rounded-xl shadow-sm"
            >
              上传文件
            </Button>
          </div>
        </div>

        {/* Decorative Circles */}
        <div className="absolute top-0 right-0 -mt-16 -mr-16 w-64 h-64 bg-white opacity-10 rounded-full blur-3xl"></div>
        <div className="absolute bottom-0 left-10 -mb-20 w-40 h-40 bg-pink-500 opacity-20 rounded-full blur-3xl"></div>
      </div>

      <Row gutter={[24, 24]}>
        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="总用户数" 
            value="1,243" 
            trend="+12% 本月" 
            trendUp={true} 
            icon={Users} 
            colorClass="text-blue-600" 
            bgClass="bg-blue-50" 
          />
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="知识库文档" 
            value="8,456" 
            trend="+5% 本周" 
            trendUp={true} 
            icon={Database} 
            colorClass="text-emerald-600" 
            bgClass="bg-emerald-50" 
          />
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="活跃任务" 
            value="42" 
            trend="-3% 今日" 
            trendUp={false} 
            icon={Activity} 
            colorClass="text-amber-600" 
            bgClass="bg-amber-50" 
          />
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <StatCard 
            title="完成率" 
            value="98.5%" 
            trend="+1.2% 本周" 
            trendUp={true} 
            icon={CheckCircle} 
            colorClass="text-purple-600" 
            bgClass="bg-purple-50" 
          />
        </Col>
      </Row>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={16}>
          <Card 
            title={<span className="font-bold text-lg text-gray-800">最近任务</span>} 
            className="h-full border-0 shadow-sm hover:shadow-md transition-shadow duration-300 rounded-2xl"
            extra={<Button type="text" icon={<MoreHorizontal size={20} className="text-gray-400" />} />}
          >
            <Table
              columns={taskColumns}
              dataSource={taskData}
              pagination={false}
              size="middle"
              className="ant-table-premium"
              rowClassName="hover:bg-gray-50 transition-colors"
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card 
            title={<span className="font-bold text-lg text-gray-800">系统状态</span>} 
            className="h-full border-0 shadow-sm hover:shadow-md transition-shadow duration-300 rounded-2xl"
          >
            <div className="space-y-6 py-2">
              {[
                { label: 'CPU 使用率', value: 45, color: '#10b981' }, // emerald-500
                { label: '内存使用率', value: 68, color: '#3b82f6' }, // blue-500
                { label: '磁盘使用率', value: 32, color: '#8b5cf6' }, // violet-500
                { label: '网络流量', value: 85, color: '#f59e0b' },   // amber-500
              ].map((item, index) => (
                <div key={index}>
                  <div className="flex justify-between mb-2">
                    <span className="text-sm font-medium text-gray-500">{item.label}</span>
                    <span className="text-sm font-bold text-gray-700">{item.value}%</span>
                  </div>
                  <Progress 
                    percent={item.value} 
                    strokeColor={item.color} 
                    trailColor="#f3f4f6"
                    showInfo={false}
                    strokeLinecap="round"
                    size="small"
                  />
                </div>
              ))}
            </div>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

