 * ```
 */
export function StatCard({
  title,
  value,
  suffix = '',
  trend,
  icon: Icon,
  iconBgColor = 'bg-blue-100',
  iconColor = 'text-blue-600',
  loading = false,
  onClick,
  className,
  style,
  extra,
}: StatCardProps) {
