/**
 * FormModal ç»„ä»¶
 *
 * @description é€šç”¨è¡¨å•å¼¹çª—ç»„ä»¶ï¼Œé›†æˆ Ant Design Form å’Œ Modal
 *
 * @example
 * ```tsx
 * <FormModal
 *   title="åˆ›å»ºç”¨æˆ·"
 *   open={isModalOpen}
 *   onClose={() => setIsModalOpen(false)}
 *   onSubmit={handleSubmit}
 *   loading={loading}
 * >
 *   <Form.Item name="username" label="ç”¨æˆ·å" rules={[{ required: true }]}>
 *     <Input placeholder="è¯·è¾“å…¥ç”¨æˆ·å" />
 *   </Form.Item>
 * </FormModal>
 * ```
 */
export function FormModal<T = Record<string, unknown>>({
  title,
  open,
  onClose,
  okText = 'ç¡®å®š',
  cancelText = 'å–æ¶ˆ',
  loading = false,
  form: externalForm,
  children,
  onSubmit,
  formProps,
  showCancel = true,
  okButtonType = 'primary',
  validateBeforeSubmit = true,
  ...modalProps
}: FormModalProps<T>) {
  const [form] = Form.useForm<T>();
  const formInstance = externalForm || form;

  const handleOk = async () => {
    if (validateBeforeSubmit) {
      try {
        const values = await formInstance.validateFields();
        await onSubmit?.(values);
      } catch (error) {
        console.error('Form validation failed:', error);
      }
    } else {
      const values = formInstance.getFieldsValue();
      await onSubmit?.(values);
    }
  };

  const handleCancel = () => {
    formInstance.resetFields();
    onClose();
  };

  return (
    <Modal
      title={title}
      open={open}
      onOk={handleOk}
      onCancel={handleCancel}
      okText={okText}
      cancelText={cancelText}
      okButtonProps={{ loading, type: okButtonType }}
      cancelButtonProps={{ style: showCancel ? undefined : { display: 'none' } }}
      destroyOnClose
      width={600}
      {...modalProps}
    >
      <Form<T>
        {...formProps}
        form={formInstance}
        layout="vertical"
      >
        {children}
      </Form>
    </Modal>
  );
}

